import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../pages/UserManagement.css"; // Correct path to UserManagement.css in pages folder

function UserManagement() {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  // Fetch users when the component mounts
  useEffect(() => {
    fetchUsers();
  }, []); // Empty dependency array ensures this only runs once on mount

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost:5000/users");
      if (response.ok) {
        const data = await response.json();
        setUsers(data); // Update the state with the fetched users
      } else {
        console.error("Error fetching users:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  // WebSocket connection for live updates (optional)
  useEffect(() => {
    const socket = new WebSocket('ws://localhost:5000/ws');
    
    socket.onopen = () => {
      console.log('WebSocket connected');
    };
    
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Received data from WebSocket:', data);
      // You can update the state based on the message received from the WebSocket
    };
    
    socket.onerror = (error) => {
      console.log('WebSocket error:', error);
    };
    
    socket.onclose = () => {
      console.log('WebSocket connection closed');
    };
    
    // Cleanup WebSocket connection on component unmount
    return () => {
      socket.close();
    };
  }, []);

  const toggleBlock = (id) => {
    setUsers(users.map(user => 
      user.id === id ? { ...user, blocked: !user.blocked } : user
    ));
  };

  const deleteUser = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  return (
    <div className="user-management-container">
      <h2>User Management</h2>
      <button onClick={() => navigate("/")}>Go to Login</button>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Last Seen</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.lastSeen || "N/A"}</td>
                <td>
                  <button onClick={() => toggleBlock(user.id)}>
                    {user.blocked ? "Unblock" : "Block"}
                  </button>
                  <button onClick={() => deleteUser(user.id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No users found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default UserManagement;
